    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        
        // showing the registration page when the register button on the login page is clicked.
        RegistrationPage registerPageObj = new RegistrationPage();
        registerPageObj.setVisible(true);
        this.dispose();

    }//GEN-LAST:event_btnRegisterActionPerformed

    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed

        String loginId = txtLoginId.getText();
        String password = new String(psfPassword.getPassword());

        // this send a boolean value to inform the client application to know whether the loginId and the password that the user entered are correct.
        /**
         * true if the information is correct
         * 
         * and 
         * 
         * false if the information is not correct
         */
         
        boolean loginStatus = loginOperation(loginId, password);

        if (loginStatus) {
            // once the user login his information such as the nickname is saved in this variable so that it can be used in other pages. 
            nickName = sendNickName(loginId);

            // saving the userId as well
            userLoginId = loginId;
            JOptionPane.showMessageDialog(null, "You Successfully Logged In!");
            ThreadOptionPage threadPageObj = new ThreadOptionPage();
            this.dispose();
            threadPageObj.setVisible(true);

        } else {
            JOptionPane.showMessageDialog(null, "Your LoginId and Password does not match. Please try again!");
        }

        txtLoginId.setText("");
        psfPassword.setText("");
    }//GEN-LAST:event_btnLoginActionPerformed

