    // INVOKING METHODS FROM THE WEBAPPLICATION
    private static boolean loginOperation(java.lang.String loginId, java.lang.String password) {
        UserService.UserService_Service service = new UserService.UserService_Service();
        UserService.UserService port = service.getUserServicePort();
        return port.loginOperation(loginId, password);
    }

    private static String sendNickName(java.lang.String loginId) {
        UserService.UserService_Service service = new UserService.UserService_Service();
        UserService.UserService port = service.getUserServicePort();
        return port.sendNickName(loginId);
    }

